insert  into `san_pham`(`san_pham_guid`,`ma_sp`,`ten_sp`,`hinh_dai_dien`,`san_pham_loai_guid`,`mo_ta_ngan`,`mo_ta_dai`,`date_create`,`date_update`) values ('28A4B790-D592-AC49-473A-9E42222C4C2E','wwwwwwwwwwwwww','','daidien_28A4B790-D592-AC49-473A-9E42222C4C2E04_16_2015_023254am.jpg','2','','                                                            ','2015-04-16 07:35:55','0000-00-00 00:00:00'),('45D2ACE6-D24E-CB65-149C-7A32C24BB3EF','TV5200sss','','daidien_45D2ACE6-D24E-CB65-149C-7A32C24BB3EF04_17_2015_045516pm.jpg','1','','<p>Mẫu kệ tivi <a title=\"phòng khách\" href=\"http://thietkethicongnhadep.net/thiet-ke-noi-that/phong-khach\">phòng khách</a> bằng kính đẹp, đơn vị chúng tôi không sản xuất mặt hàng này.  Tuy nhiên tôi nhận thấy những mẫu kệ tivi bằng kính này khá đẹp và giá cả khá hợp lý nên up lên một vài mẫu để các bạn có thể tham khảo trang bị cho <a title=\"Thiết kế nội thất phòng khách cao cấp\" href=\"http://thietkethicongnhadep.net/xuong-do-go-noi-that/thiet-ke-noi-that-phong-khach-cao-cap\" target=\"_blank\">nội thất phòng khách</a> nhà mình. Chúng tôi hy vọng những mẫu kệ dưới đây sẽ giúp các bạn được phần nào.</p>\r\n<p>Mức giá của những kệ tivi phòng khách bằng kính dao động từ (2 triệu – 3 triệu) nhé. Nếu bạn nào có nhu cầu mua thì có thể lấy mẫu và mang đi tham khảo các đơn vị khác. Nếu các bạn có nhu cầu mua <a title=\"Đóng kệ tivi giá rẻ\" href=\"http://thietkethicongnhadep.net/xuong-do-go-noi-that/dong-ke-tivi-gia-re\" target=\"_blank\">kệ tivi gỗ tự nhiên</a> thì có thể liên hệ với chúng tôi, giá của kệ ti vi bằng gỗ tự nhiên sẽ khá cao so với chiếc kệ ti vi bằng kinh. Chúc các bạn vui vẻ!</p>\r\n<h2>Kệ tivi phòng khách bằng kính đẹp</h2>\r\n<div id=\"attachment_9611\" class=\"wp-caption alignnone\"><a href=\"http://thietkethicongnhadep.net/wp-content/uploads/2013/08/ke-tivi-phong-khach-bang-kinh-1.jpg\" rel=\"lightbox[9610]\"><img class=\" wp-image-9611 \" title=\"Kệ tivi phòng khách bằng kính đẹp\" src=\"http://thietkethicongnhadep.net/wp-